// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

contract ProductRegistry {
    enum ProductStatus { MANUFACTURING, DISTRIBUTING, FOR_SALE, SOLD }

    struct Product {
        uint256 id;
        string name;
        string description;
        string serialNumber;
        string creatorEmail;
        string currentOwnerEmail;
        ProductStatus status;
        uint256 manufactureDate;
        uint256 createdAt;
        uint256 companyId;
        string companyName;
        uint256 onChainTimestamp; // ✅ 新增：数据上链时间
    }

    uint256 private productCounter = 1;

    mapping(uint256 => Product) public products;
    mapping(string => uint256) public serialNumberToProductId; // serialNumber -> productId

    event ProductRegistered(uint256 indexed productId, string serialNumber);
    event OwnershipTransferred(uint256 indexed productId, string fromEmail, string toEmail);
    event StatusUpdated(uint256 indexed productId, ProductStatus newStatus);

    // --- 商品登记 ---
    function registerProduct(
        string calldata name,
        string calldata description,
        string calldata serialNumber,
        string calldata creatorEmail,
        uint256 manufactureDate,
        uint256 createdAt,
        uint256 companyId,
        string calldata companyName
    ) external {
        require(serialNumberToProductId[serialNumber] == 0, "Serial number already registered");

        uint256 newProductId = productCounter++;
        products[newProductId] = Product({
            id: newProductId,
            name: name,
            description: description,
            serialNumber: serialNumber,
            creatorEmail: creatorEmail,
            currentOwnerEmail: creatorEmail, // 初始持有人就是录入者
            status: ProductStatus.MANUFACTURING,
            manufactureDate: manufactureDate,
            createdAt: createdAt,
            companyId: companyId,
            companyName: companyName,
            onChainTimestamp: block.timestamp // ✅ 上链时间就是注册时的链上时间
        });

        serialNumberToProductId[serialNumber] = newProductId;

        emit ProductRegistered(newProductId, serialNumber);
    }

    // --- 商品转移 ---
    function transferOwnership(uint256 productId, string calldata newOwnerEmail) external {
        Product storage product = products[productId];
        product.currentOwnerEmail = newOwnerEmail;

        emit OwnershipTransferred(productId, product.creatorEmail, newOwnerEmail);
    }

    // --- 修改商品状态 ---
    function updateProductStatus(uint256 productId, ProductStatus newStatus) external {
        Product storage product = products[productId];
        product.status = newStatus;

        emit StatusUpdated(productId, newStatus);
    }

    // --- 查询 ---
    function getProductById(uint256 productId) external view returns (Product memory) {
        return products[productId];
    }

    function getProductBySerialNumber(string calldata serialNumber) external view returns (Product memory) {
        uint256 productId = serialNumberToProductId[serialNumber];
        require(productId != 0, "Product not found");

        return products[productId];
    }
}
